mapper.select:
SELECT re.review_no, od.order_detail_no, re.review_star, re.review_title, re.review_content, re.review_date, pro.product_thumb_img,pro.product_thumb_img, pro.product_name, re.mem_id
FROM tbl_review re, tbl_order_detail od, tbl_product pro
WHERE re.order_detail_no = od.order_detail_no AND od.product_no = pro.product_no AND re.mem_id = #{mem_id}

SELECT COUNT(*)
FROM tbl_review
WHERE order_detail_no=#{order_detail_no}

SELECT re.review_no, od.order_detail_no, re.review_star, re.review_title, re.review_content, re.review_date, pro.product_thumb_img,pro.product_thumb_img, pro.product_name, re.mem_id
FROM tbl_review re, tbl_order_detail od, tbl_product pro
WHERE re.order_detail_no = od.order_detail_no AND od.product_no = pro.product_no AND re.review_no = #{review_no}

mapper.update:
UPDATE tbl_review
SET review_title= #{review_title},
review_star= #{review_star},
review_content= #{review_content}
WHERE review_no= #{review_no}

mapper.select:
SELECT re.review_no, re.review_star, re.review_title, re.review_content, re.review_date, pro.product_thumb_img,pro.product_thumb_img, pro.product_name, re.mem_id
FROM tbl_review re, tbl_product pro ,tbl_order_detail od
WHERE re.order_detail_no = od.order_detail_no AND od.product_no = pro.product_no AND pro.product_no = #{product_no}
