mapper.select:
SELECT mem.mem_id,mem_pwd,mem_name,mem_email,mem_tel,mem_joindate,enabled, auth
FROM tbl_member mem LEFT OUTER JOIN tbl_member_auth auth on mem.mem_id = auth.mem_id
WHERE mem.mem_id = #{mem_id}

mapper.update:
UPDATE tbl_member
SET 
mem_email = #{mem_email},
mem_tel = #{mem_tel},
mem_pwd = #{mem_pwd}
WHERE mem_id = #{mem_id}
